<script>
import "bootstrap/dist/css/bootstrap.min.css";
import pdfMake from 'pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import htmlToPdfmake from 'html-to-pdfmake';

import Axios from 'axios'
import DataTable from 'primevue/datatable';
import Column from 'primevue/column';
import 'primevue/resources/primevue.min.css';
import 'primevue/resources/themes/mdc-light-indigo/theme.css';
import 'primeicons/primeicons.css';
import Dropdown from 'primevue/dropdown';
export default {
	name: 'AdmReports',
	components: {
	DataTable,
	Column,
	Dropdown,
	pdfMake,
	pdfFonts,
	htmlToPdfmake
	},
	data () {
		return {
			start_date: null,
			end_date: null,
			driver: null,
			driver_id: null,
			loader_id: null,
			loader: null,
			workshop_id: null,
			workshop: null,
			selected: null,
			ret_table: [],
			showtable: false
		}	
	},
	methods: {
		to_admins () {
			this.showtable = false
			this.$store.commit('HideAdminReports')
			this.$store.commit('ShowAdmins')
			console.log("HideAdminReports==",this.$store.state.isShowAdminReports)
		},
		get_stat () {
			this.showtable = true
		//	console.log("this.showtable==",this.showtable)
		//	console.log("get_stat==",this.start_date,"==",this.end_date,"==",this.driver_id,"--", this.loader_id,"--",this.workshop_id)
			Axios.get(this.$store.state.server_url+'statbyiddate/'+this.start_date+'/'+this.end_date+'/'+ this.driver_id+'/'+this.loader_id+'/'+this.workshop_id)
				.then((response) => {
					console.log(response)
					this.ret_table = response.data
				})
				.catch(error => console.log(error))		
		},
		printDocument() {
		//get table html
		const pdfTable = document.getElementById('divToPrint');
		//html to pdf format
		var html = htmlToPdfmake(pdfTable.innerHTML);
		const documentDefinition = { content: html, pageOrientation: 'landscape' };
		pdfMake.vfs = pdfFonts.pdfMake.vfs;
		pdfMake.createPdf(documentDefinition).open();
			
		}
	}
}
</script>
